$ticket_key = $_ENV['TICKET_TOKEN'] ?? '';

if (!$ticket_key) {
    echo json_encode(['status' => 'error', 'message' => 'JWT secret tanımlı değil.']);
    exit;
}

$ticket = isset($_GET['ticket']) ? trim($_GET['ticket']) : '';

if (empty($ticket)) {
    echo json_encode(['status' => 'error', 'message' => 'Token gönderilmedi.']);
    exit;
}

$result = verifyJWT($ticket, $ticket_key);

if (!$result['valid']) {
    echo json_encode(['status' => 'error', 'message' => $result['message']]);
    exit;
}

$data = $result['data'];

$balance = getBalance($data['user_id']);
$booked_seats = getSeats(1);

$ticket_info=getTicketInfo($data['trip_id']);
$arrival_parts = explode(' ', $ticket_info['arrival_time']);
$arrival_date = $arrival_parts[0];   
$arrival_time = $arrival_parts[1];




